Account_Name_1.query:
q = load "Cloud_Services_Bookings_Detail_Data_File_RL";
q= filter q by {{column(step_listselector_geo_FFF.selection,["CSG_Geo"]).asEquality("Geo_Roll_Up")}};
q= filter q by {{column(step_listselector_region_FFF.selection,["CSG_Region"]).asEquality("CSG_Region_Roll_Up")}};
q= filter q by {{column(step_listselector_subregion_FFF.selection,["CSG_Subregion"]).asEquality("BM_Territory_Level_2")}};
q= filter q by {{column(step_listselector_portfolio_FFF.selection,["CSG_Portfolio"]).asEquality("CSG_Qual_Opp_Portfolio")}};
q = group q by '{{column(Static_Grouping_mobile.selection,["value"]).asObject()}}';
q = foreach q generate '{{column(Static_Grouping_mobile.selection,["value"]).asObject()}}' as '{{column(Static_Grouping_mobile.selection,["value"]).asObject()}}', sum('Bookings') as 'sum_Bookings';
q = order q by 'sum_Bookings' {{column(Static_sort_order_mobile.selection,["value"]).asObject()}};
q = limit q 10;

