Annual_Target_Metric_2.query:
q = load "FY18_CSG_RL_SL_PL_Metric_Scorecard";
q = filter q by ('Region' in {{coalesce(column(step_listselector_portfolio_FFF.selection,["Console_Portfolio"]), column(step_listselector_subregion_FFF.selection,["Console_Subregion"]), column(step_listselector_region_FFF.selection,["Console_Region"]), column(step_listselector_geo_FFF.selection,["Geo"]), column(step_listselector_ww_FFF.selection,["Geo"]), "[]").asString()}});
q = filter q by 'Metric' in ["EWS Growth (Operational)"];
q = group q by ('Display_Type','Region','Console_Segment');
q = foreach q generate 'Display_Type','Region','Console_Segment',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('Annual_Target_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('Annual_Target_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('Annual_Target_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Annual_Target_Metric',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('YTD_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('YTD_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('YTD_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_YTD_Metric',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Change_in_YTD_MoM_Metric', (case  when 'Display_Type' == "Dollar" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Numeric" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Percent" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Change_in_Attainment_MoM_Pct',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Numeric" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Percent" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_YTD_Attainment_Pct',first('Colorization_Hex_Code') as 'Colorization_Hex_Code',(case when (avg('Change_in_Attainment_MoM_Pct') < -0.005) then "#C73737" when (avg('Change_in_Attainment_MoM_Pct') > 0.005) then "#3ABC4A" else "#F6A623" end) as 'Color';
q = limit q 200;

