Annual_Target_Metric_1.query:
q = load "FY19_CSG_RL_SL_PL_Metric_Scorecard";
q = filter q by ('Region' in {{coalesce(column(step_listselector_portfolio_FFF.selection,["Console_Portfolio"]), column(step_listselector_subregion_FFF.selection,["Console_Subregion"]), column(step_listselector_region_FFF.selection,["Console_Region"]), column(step_listselector_geo_FFF.selection,["Geo"]), column(step_listselector_ww_FFF.selection,["Geo"]), "[]").asString()}});
q = filter q by 'Metric' == "Services Margin";
q = group q by ('Display_Type','Region','Console_Segment',  'Scorecard');
q = foreach q generate 'Display_Type','Region','Console_Segment','Scorecard',(case when 'Display_Type' == "Dollar" then number_to_string(sum('Annual_Target_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('Annual_Target_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('Annual_Target_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Annual_Target_Metric',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('YTD_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('YTD_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('YTD_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_YTD_Metric',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "$#,##0.0;-$#,##0.0")  when 'Display_Type' == "Numeric" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "#,##0;-#,##0")  when 'Display_Type' == "Percent" then number_to_string(sum('Change_in_YTD_MoM_Metric'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Change_in_YTD_MoM_Metric', (case  when 'Display_Type' == "Dollar" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Numeric" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Percent" then number_to_string(sum('Change_in_Attainment_MoM_Pct'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_Change_in_Attainment_MoM_Pct',(case  when 'Display_Type' == "Dollar" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Numeric" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  when 'Display_Type' == "Percent" then number_to_string(sum('YTD_Attainment_Pct'), "#,##0.0%;-#,##0.0%")  else "--" end) as 'sum_YTD_Attainment_Pct',first('Colorization_Hex_Code') as 'Colorization_Hex_Code',(case when (avg('Change_in_Attainment_MoM_Pct') < -0.005) then "#C73737" when (avg('Change_in_Attainment_MoM_Pct') > 0.005) then "#3ABC4A" else "#F6A623" end) as 'Color';
q = limit q 200;

Year_Month_1.query:
q = load "FY19_CSG_RL_SL_PL_Metric_Scorecard";
q = filter q by ('Region' in {{coalesce(column(step_listselector_portfolio_FFF.selection,["Console_Portfolio"]), column(step_listselector_subregion_FFF.selection,["Console_Subregion"]), column(step_listselector_region_FFF.selection,["Console_Region"]), column(step_listselector_geo_FFF.selection,["Geo"]), column(step_listselector_ww_FFF.selection,["Geo"]), "[]").asString()}});
q = filter q by 'Metric' == "Services Margin";
q = group q by 'Year_Month';
q = foreach q generate 'Year_Month' as 'Year_Month', sum('{{column(Static_Grouping_5.selection,["value"]).asObject()}}') as '{{column(Static_Grouping_5.selection,["axis"]).asObject()}}';
q = order q by 'Year_Month' asc;
q = limit q 20;
